/*
 * 验证用户名及密码
 */
package MongoDB;

import com.mongodb.MongoClient;
import com.mongodb.MongoCredential;
import com.mongodb.ServerAddress;
import com.mongodb.client.MongoDatabase;

import java.util.*;
public class MongoDBJDBC02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		try{
			//连接MongoDB服务，若是远程连接可以替换"localhost"为服务器所在的IP地址
			//ServerAddress()两个参数：服务器地址、端口
			ServerAddress serverAddress=new ServerAddress("localhost",27017);
			List<ServerAddress>addrs=new ArrayList<ServerAddress>();
			addrs.add(serverAddress);
			//mongoCredential.createScramSha1Credential()参数分别为：用户名、数据库名称、密码
			MongoCredential credential=MongoCredential.createScramSha1Credential("userName", "databaseName", "password".toCharArray());
			List<MongoCredential> credentials=new ArrayList<MongoCredential>();
			credentials.add(credential);
			//通过连接认证获取MongoDB连接
			MongoClient mongoClient=new MongoClient(addrs,credentials);
			//连接到数据库
			MongoDatabase mongoDatabase=mongoClient.getDatabase("databaseName");
			System.out.println("Connnect to database successfully");
		}catch(Exception e){
			System.err.println(e.getClass().getName()+":"+e.getMessage());
		}
		
		
	}

}
